<div style="border-bottom: 2px solid #eee; padding-bottom: 8px; color: #333; font-family: sans-serif;">

<!--
    =================== DYNAMIC DATA SETUP ===================
    This block checks the company and sets all the necessary variables.
    The HTML below will then use these variables.
-->
{% set wbh_companies = ["WIN BORN HOLDING CO., LTD.", "WIN BORN LUXE CO., LTD.", "WIN BORN E COMMERCE CO., LTD.", "WIN BORN CONSTRUCTION CO., LTD."] %}

{% if doc.company in wbh_companies %}
    {% set logo_url = "/files/wbh_logo.png" %}
    {% set eng_name = "SIAM WIN BORN COMPANY LIMITED (HEADQUARTERS)" %}
    {% set thai_name = "บริษัท สยาม วิน บอร์น จำกัด (สำนักงานใหญ่)" %}
    {% set address = "295 หมู่ที่ 2 ตำบลมาบยางพร อำเภอปลวกแดง จ.ระยอง 21140" %}
    {% set tax_id = "0205553011513" %}
    {% set phone = "+66 96-085-0808" %}
    {% set email = "admin@winbornholding.com" %}

{% elif doc.company == "YUMM CO., LTD." %}
    {% set logo_url = "/files/wby_logo.png" %}
    {% set eng_name = "SIAM WIN BORN COMPANY LIMITED (HEADQUARTERS)" %}
    {% set thai_name = "บริษัท สยาม วิน บอร์น จำกัด (สำนักงานใหญ่)" %}
    {% set address = "295 หมู่ที่ 2 ตำบลมาบยางพร อำเภอปลวกแดง จ.ระยอง 21140" %}
    {% set tax_id = "0205553011513" %}
    {% set phone = "+66 96-085-0808" %}
    {% set email = "admin@winbornholding.com" %}

{% elif doc.company == "KOBMICRO MUSICCENTER CO., LTD." %}
    <!-- This is now a specific condition instead of a default 'else' -->
    {% set logo_url = "/files/kmc_logo.png" %}
    {% set eng_name = "KOBMICRO MUSICCENTER COMPANY LIMITED (HEADQUARTERS)" %}
    {% set thai_name = "บริษัท กบไมโครมิวสิกเซนเตอร์ จำกัด (สำนักงานใหญ่)" %}
    {% set address = "165/38 หมู่ที่ 10 ตำบลสุรศักดิ์ อำเภอศรีราชา จ.ชลบุรี 20110" %}
    {% set tax_id = "0205560039657" %}
    {% set phone = "+66 98-898-7793" %}
    {% set email = "kmc@winbornholding.com" %}
{% endif %}
<!-- ================= END OF DYNAMIC DATA SETUP ================= -->


<!-- 
    =================== HTML STRUCTURE ===================
    This is the single, clean HTML structure that uses the variables defined above.
-->
    <!-- Top Row: Logo and Document Title/Number -->
    <div style="display: flex; justify-content: space-between; align-items: flex-start; margin-bottom: 8px;">
        <div>
            <img src="{{ logo_url }}" alt="Company Logo" style="height: 60px; width: auto;" onerror="this.onerror=null;this.src='https://placehold.co/200x60/cccccc/000000?text=Logo+Not+Found';">
        </div>
        <div style="text-align: right;">
            <h2 style="font-size: 24px; font-weight: bold; color: #000; margin: 0;">
                {{ doc.doctype }} / ใบเสนอราคาจากผู้ขาย
            </h2>
            <p style="margin: 8px 0 0 0; color: #000 !important; font-size: 12px;">No./เลขที่ : <strong style="font-weight: bold;">{{ doc.name }}</strong></p>
        </div>
    </div>

    <!-- Main Content Row -->
    <div style="display: flex; justify-content: space-between; align-items: stretch;">

        <!-- Left Side: Company Address Block -->
        <div style="width: 55%;">
            <p style="margin: 2px 0; line-height: 1.5; font-weight: bold;">{{ eng_name }}</p>
            <p style="margin: 2px 0; line-height: 1.5; font-weight: bold;">{{ thai_name }}</p>
            <p style="margin: 2px 0; line-height: 1.5;">{{ address }}</p>
            <p style="margin: 2px 0; line-height: 1.5;">Tax ID/เลขประจำตัวผู้เสียภาษี : <span style="font-weight: bold;">{{ tax_id }}</span></p>
        </div>

        <!-- Right Side: Details Block -->
        <div style="width: 40%; text-align: right; display: flex; flex-direction: column;">
            <div>
                <p style="margin: -4px 0; font-size: 12px;">Date/วันที่ : <strong style="font-weight: bold;">{{ frappe.utils.formatdate(doc.transaction_date, "dd-MM-yyyy") }}</strong></p>
            </div>
            <div style="flex-grow: 1;"></div>
            <div>
                <p style="margin: -1px 0 4px 0;">Phone No./เบอร์โทร : {{ phone }}</p>
                <p style="margin: 0;">Email/อีเมล : {{ email }}</p>
            </div>
        </div>
    </div>

</div>
